const toYueDuUrl = (url) => {
  fetch(url)
    .then((resp) => resp.text())
    .then((text) => {
      let isNext=false
      const matchArray1 = ["yuedu://", ".json"];
      const regX1 = RegExp(
        `\\${matchArray1[0]}+.*?([\\s\\S]*?)${matchArray1[1]}.*?`,
        "g"
      );
      text.replace(regX1, function (word, key) {
        if(word){
          isNext=true
          window.location.href = word;
        }
       
      });
      if(isNext)return;
      const matchArray2 = ["yuedu://", ".htm"];
      const regX2 = RegExp(
        `\\${matchArray2[0]}+.*?([\\s\\S]*?)${matchArray2[1]}.*?`,
        "g"
      );
      text.replace(regX2, function (word, key) {
        if(word){
          isNext=true
          window.location.href = word;
        }
      });
      if(isNext)return;
      const matchArray3 = ["yuedu://", ".html"];
      const regX3 = RegExp(
        `\\${matchArray3[0]}+.*?([\\s\\S]*?)${matchArray3[1]}.*?`,
        "g"
      );
      text.replace(regX3, function (word, key) {
        if(word){
          isNext=true
          window.location.href = word;
        }
      });
      if(isNext)return;
      const matchArray4 = ["yuedu://", ".txt"];
      const regX4 = RegExp(
        `\\${matchArray4[0]}+.*?([\\s\\S]*?)${matchArray4[1]}.*?`,
        "g"
      );
      text.replace(regX4, function (word, key) {
        if(word){
          isNext=true
          window.location.href = word;
        }
      });
    });
};
