huan = (index) =>{
		t1 = '.jiekou button'
		t2 = '.jishu div'
		omit($(t1+'.active,'+t2+'.active'))
		active($(t1),index)
		return active($(t2),index)
	}


//选择集数
xuan = (item,type)=>{
		type = type==0?'jiexi':'jishu';
		btn = $('.'+type+' button.active');

		if(item.value==btn[0].value)return;
		omit(btn); item.className='active'
		return geturl()
	}


//获取资源链接url=decodeHexUrl(url)

function geturl(){
	url=$('.jishu button.active')[0].value.trim();
	fetch(url).then(res=>res.text())
		.then(ajaxhtml=>{
				url = String(ajaxhtml).match(/[\s\S]*url":"([^"]*)/)[1].replace(/\\/g,'').trim();

   url=decodeURIComponent(url);
	         if(/^\s*$/.test(url))return;

	            url = $('.jiexi button.active')[0].value.trim()+url;
	          return $('iframe')[0].src = $('body p')[0].innerText = url
			})
		.catch(ajaxerr=>{
				return $('body p')[0].innerText = '获取失败，请重新尝试';
			});
}

function decodeHexUrl(url) {
    return decodeURIComponent(url);
}

//初始化
(()=>{
		active($('.jiexi button'),0)
		active($('.jiekou button'),0)
		active($('.jishu div'),0)
		active($('.jishu button'),0)
		return geturl()
	})()
</script>
