a = String(source.sourceComment);
reg1 = /\s*『.*?』\s*/g
reg2 = /.+《(.*?)》.*/
sortName = []

b = a.match(reg1);
b = a.split(reg1).map((list,i)=>{
if(!/^\s*$/.test(list)){
	try{
		sort=b[i-1].match(/『(.*?)』/)[1]+"::";
	}catch(err){sort="::"}

		url=[];
		list.split(/\n|&&|•/).map(text=>{
		if( (y=text.split('::')).length==2 && /^\s*::/.test(sort) )sort+='\n'+y[0]+'::'+y[1];
		if( reg2.test(text) && !/^\s*::/.test(sort) )url.push(text);
			});
		return sort+=url.join('•');
	}
}).map(text=>{
		text = String(text).replace(/^\s*::\s*|\s*::\s*$/g,'');

if(!/\n/.test(text)&&/《/.test(text))text = text.replace(/^(.+::)/,'$1https://www.baidu.com/?wd=');
if(/::/.test(text)){
		sortName.push(text.replace(/::.*/g,''))
		return text
	}
}).join('\n');

function set(json){
		return source.setVariable(JSON.stringify(json));
	}

function get(){
try{
v=JSON.parse(source.getVariable());
}catch(e){v=null}
if(v==null)v={};
}
get()

sortName = sortName.join(',').replace(/\s*\n\s*/g,',').split(',').map($=>{
	if(v[$]==undefined)v[$]=1;
	return $.trim();
	}).join(',');
b = b.split('\n').map(text=>{
		return v[text.trim().split('::')[0]]?text.trim():""
	}).join('\n');
set(v);


//log字符串
function login(t){
		if(t==undefined)return;
		java.log(t);
		java.toast(t);
	}


//log当前分类状态
function look(){get();
if(JSON.stringify(v)=="{}")$('全部分类');
		t="【分类状态】";
		for(key in v)t+='\n\t'+key+'=>'+(v[key]?'启用':'禁用');
		return login(t);
}


//启用/禁用
function $(sort,name){get();
if(name==undefined)name=sort.split(',')[0];
name = name.trim()
if(name=='全部分类'){
		sort = sortName;
	}
else{//屏蔽错误分类
		sort1 = []
		sort2 = sortName.split(',')
		sort.split(',').map(key1=>{
				sort2.map(key2=>{
						if(key1.trim()==key2)sort1.push(key1.trim());
					})
			})
		sort = sort1.join(',')
	}
if(!/^\s*$/.test(sort)){
		sort = String(sort).split(',');
		f = sort.length>1?'分组':'分类';
		V = v[sort[0]];
		if(name=='全部分类')v={};
		login('【更改成功】\n『'+f+'』 '+name+': 已'+(V?'禁用':'启用'));
		sort.map(key=>v[key]=V?0:1);
		return set(v);
	}
return login('【无法更改】\n原因：没有有效分类(分类名称不正确)')
}

